import itertools
class Solution(object):
    def palindromePairs(self, words):
        """
        :type words: List[str]
        :rtype: List[List[int]]
        """        
        newlist=list(itertools.permutations(words,2))

        return_list = []
        for item in newlist:
            new_str=''.join(item)
            palin=new_str[::-1]
            if (new_str == palin):
                return_list.append([words.index(item[0]), words.index(item[1])])
        return return_list

